import React, { useState, useRef } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

import { CheckMarker, Time } from 'components'
import { voiceDiagramIcon, playIcon, pauseIcon } from 'assets/icons'

import './Message.scss'

const Message = ({
  avatar,
  user,
  text,
  date,
  attachments,
  audioFile,
  isMine,
  isReaded,
  isTyping,
}) => {
  const [isPlaying, setIsPlaying] = useState(false)
  const audioRef = useRef(null)

  const togglePlay = () => {
    if (isPlaying) {
      audioRef.current.stop()
    } else {
      audioRef.current.play()
    }
  }

  const handleAudioClick = () => {
    togglePlay()
    setIsPlaying(!isPlaying)
  }

  return (
    <div
      className={classNames('Message', {
        'Message--my': isMine,
        'Message--typing': isTyping,
        'Message--audio': audioFile !== '',
        'Message--image': attachments.length === 1,
      })}>
      <div className="Message__avatar">
        <img src={avatar} alt={`Avatar ${user.fullName}`} />
      </div>
      <div className="Message__info">
        <div className="Message__content">
          {(audioFile || text || isTyping) && (
            <div className="Message__bubble">
              {text && <p className="Message__text">{text}</p>}

              {isTyping && (
                <div className="Message__typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
              )}

              {audioFile && (
                <div className="Message__audio">
                  <audio src={audioFile} ref={audioRef} preload />
                  <div
                    className="Message__audio-progress"
                    style={{ width: '40%' }}></div>
                  <div className="Message__audio-info">
                    <div className="Message__audio-btn">
                      <button type="button" onClick={() => handleAudioClick()}>
                        <img
                          src={isPlaying ? pauseIcon : playIcon}
                          alt={isPlaying ? 'Pause' : 'Play'}
                        />
                      </button>
                    </div>
                    <div className="Message__audio-diagram">
                      <img src={voiceDiagramIcon} alt="Voice diagram" />
                    </div>
                    <span className="Message__audio-duration">0:22</span>
                  </div>
                </div>
              )}
            </div>
          )}
          {!!attachments.length && (
            <ul className="Message__attachments">
              {attachments.map((attach, index) => (
                <li className="Message__attachments-item" key={index}>
                  <img src={attach.url} alt={attach.fileName} />
                </li>
              ))}
            </ul>
          )}
          {date && <Time value={date} />}
        </div>
      </div>
      {isMine && <CheckMarker checked={isReaded} />}
    </div>
  )
}

Message.propTypes = {
  avatar: PropTypes.string,
  user: PropTypes.object,
  text: PropTypes.string,
  date: PropTypes.number,
  attachments: PropTypes.array,
  audioFile: PropTypes.string,
  isMine: PropTypes.bool,
  isReaded: PropTypes.bool,
  isTyping: PropTypes.bool,
}

Message.defaultProps = {
  user: {},
  attachments: [],
  audioFile: '',
  isMine: false,
  isReaded: false,
  isTyping: false,
}

export default Message
